"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[66662],{23758:(n,e,t)=>{t.r(e),t.d(e,{assets:()=>l,contentTitle:()=>s,default:()=>h,frontMatter:()=>r,metadata:()=>i,toc:()=>c});const i=JSON.parse('{"id":"Foreign Policy/Political Issues/International Law","title":"      7. Upholding and Strengthening International Law","description":"Committing to a Rules-Based International Order","source":"@site/docs/5. Foreign Policy/5. Political Issues/7. International Law.md","sourceDirName":"5. Foreign Policy/5. Political Issues","slug":"/Foreign Policy/Political Issues/International Law","permalink":"/firstpub-Platform/docs/Foreign Policy/Political Issues/International Law","draft":false,"unlisted":false,"editUrl":"https://github.com/unitedprogressparty/firstpub-Platform/tree/main/docs/5. Foreign Policy/5. Political Issues/7. International Law.md","tags":[],"version":"current","sidebarPosition":7,"frontMatter":{"title":"      7. Upholding and Strengthening International Law"},"sidebar":"tutorialSidebar","previous":{"title":"      6. Enhancing Global Cybersecurity and Deterrence","permalink":"/firstpub-Platform/docs/Foreign Policy/Political Issues/Cybersecurity"},"next":{"title":"      8. Enhancing U.S. Soft Power and Public Diplomacy","permalink":"/firstpub-Platform/docs/Foreign Policy/Political Issues/Soft Power"}}');var a=t(74848),o=t(28453);const r={title:"      7. Upholding and Strengthening International Law"},s=void 0,l={},c=[{value:"Committing to a Rules-Based International Order",id:"committing-to-a-rules-based-international-order",level:2},{value:"Key Strategies for Upholding International Law:",id:"key-strategies-for-upholding-international-law",level:3},{value:"Conclusion:",id:"conclusion",level:3}];function d(n){const e={h2:"h2",h3:"h3",li:"li",ol:"ol",p:"p",strong:"strong",ul:"ul",...(0,o.R)(),...n.components};return(0,a.jsxs)(a.Fragment,{children:[(0,a.jsx)(e.h2,{id:"committing-to-a-rules-based-international-order",children:"Committing to a Rules-Based International Order"}),"\n",(0,a.jsx)(e.p,{children:"International law\u2014encompassing treaties, customary practices, and the rulings of international courts and tribunals\u2014provides an essential framework for governing relations between states, regulating global commons, protecting human rights, and promoting peace and security. A stable, predictable, and just international order depends on widespread adherence to and respect for international law. While the system is imperfect and enforcement can be challenging, upholding international law is crucial for managing global interdependence and preventing a descent into anarchy or rule by force."}),"\n",(0,a.jsx)(e.p,{children:"Our platform commits the United States to strengthening its commitment to international law and leading efforts to ensure its development and enforcement. We believe that U.S. adherence to international legal norms enhances its legitimacy and influence, strengthens alliances, and ultimately serves its long-term security interests."}),"\n",(0,a.jsx)(e.h3,{id:"key-strategies-for-upholding-international-law",children:"Key Strategies for Upholding International Law:"}),"\n",(0,a.jsxs)(e.ol,{children:["\n",(0,a.jsxs)(e.li,{children:["\n",(0,a.jsx)(e.p,{children:(0,a.jsx)(e.strong,{children:"Accede to Key International Treaties:"})}),"\n",(0,a.jsxs)(e.ul,{children:["\n",(0,a.jsx)(e.li,{children:"Pursue ratification of or accession to fundamental international treaties where the U.S. is not yet a party, such as the UN Convention on the Law of the Sea (UNCLOS), the Rome Statute of the International Criminal Court (ICC) (with appropriate consideration of U.S. constitutional concerns and safeguards), the Convention on the Elimination of All Forms of Discrimination Against Women (CEDAW), and the Convention on the Rights of the Child (CRC). Joining these treaties strengthens the international legal framework and U.S. leadership."}),"\n"]}),"\n"]}),"\n",(0,a.jsxs)(e.li,{children:["\n",(0,a.jsx)(e.p,{children:(0,a.jsx)(e.strong,{children:"Respect and Support International Courts and Tribunals:"})}),"\n",(0,a.jsxs)(e.ul,{children:["\n",(0,a.jsx)(e.li,{children:"Engage constructively with international judicial bodies like the International Court of Justice (ICJ) and support the work of international criminal tribunals (including the ICC, where appropriate) aimed at ensuring accountability for genocide, war crimes, and crimes against humanity."}),"\n",(0,a.jsx)(e.li,{children:"Cooperate with international investigations and prosecutions consistent with U.S. law and interests."}),"\n"]}),"\n"]}),"\n",(0,a.jsxs)(e.li,{children:["\n",(0,a.jsx)(e.p,{children:(0,a.jsx)(e.strong,{children:"Incorporate International Law into Domestic Policy:"})}),"\n",(0,a.jsxs)(e.ul,{children:["\n",(0,a.jsx)(e.li,{children:"Ensure U.S. domestic laws and foreign policy actions are consistent with its international legal obligations, including treaty commitments and customary international law regarding human rights, the use of force, and humanitarian law."}),"\n",(0,a.jsx)(e.li,{children:"Strengthen mechanisms within the executive and legislative branches for reviewing the international legal implications of proposed actions."}),"\n"]}),"\n"]}),"\n",(0,a.jsxs)(e.li,{children:["\n",(0,a.jsx)(e.p,{children:(0,a.jsx)(e.strong,{children:"Promote the Peaceful Settlement of Disputes:"})}),"\n",(0,a.jsxs)(e.ul,{children:["\n",(0,a.jsx)(e.li,{children:"Utilize and support international mechanisms for the peaceful settlement of disputes between states, including negotiation, mediation, arbitration, and judicial settlement via the ICJ."}),"\n"]}),"\n"]}),"\n",(0,a.jsxs)(e.li,{children:["\n",(0,a.jsx)(e.p,{children:(0,a.jsx)(e.strong,{children:"Champion International Humanitarian Law (IHL):"})}),"\n",(0,a.jsxs)(e.ul,{children:["\n",(0,a.jsx)(e.li,{children:"Uphold and promote respect for IHL (the laws of war) in all contexts. Ensure U.S. military operations comply with IHL principles of distinction, proportionality, and precaution, and advocate for adherence by all parties to conflicts."}),"\n",(0,a.jsx)(e.li,{children:"Support the work of the International Committee of the Red Cross (ICRC) and other humanitarian organizations."}),"\n"]}),"\n"]}),"\n",(0,a.jsxs)(e.li,{children:["\n",(0,a.jsx)(e.p,{children:(0,a.jsx)(e.strong,{children:"Develop International Law for Emerging Challenges:"})}),"\n",(0,a.jsxs)(e.ul,{children:["\n",(0,a.jsx)(e.li,{children:"Lead efforts to develop international legal frameworks for new global challenges, such as cybersecurity norms, governance of artificial intelligence, space activities, and climate change response, ensuring these frameworks are grounded in human rights and equity."}),"\n"]}),"\n"]}),"\n"]}),"\n",(0,a.jsx)(e.h3,{id:"conclusion",children:"Conclusion:"}),"\n",(0,a.jsx)(e.p,{children:"A strong commitment to international law is essential for a stable, predictable, and just world order. By acceding to key treaties, supporting international courts, ensuring domestic compliance, promoting peaceful dispute settlement, and leading efforts to develop law for new challenges, the United States can strengthen the rules-based international system, enhance its global leadership, and advance both its interests and its values. Upholding international law is not a constraint on sovereignty but an investment in collective security and shared prosperity."})]})}function h(n={}){const{wrapper:e}={...(0,o.R)(),...n.components};return e?(0,a.jsx)(e,{...n,children:(0,a.jsx)(d,{...n})}):d(n)}},28453:(n,e,t)=>{t.d(e,{R:()=>r,x:()=>s});var i=t(96540);const a={},o=i.createContext(a);function r(n){const e=i.useContext(o);return i.useMemo((function(){return"function"==typeof n?n(e):{...e,...n}}),[e,n])}function s(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(a):n.components||a:r(n.components),i.createElement(o.Provider,{value:e},n.children)}}}]);